import React from 'react'
import {Jumbotron, ModalBody, ModalHeader,Modal} from 'reactstrap'
import ListView from './listView.jsx'
import Rowview from './table'
import Todofrom from './todoFrom.jsx'
// import SearchBox from './controllerBox.jsx'


class Head extends React.Component{
    state={
        todo:[
          
        ],
        isOpenModalTodo:false,
        searchTask:'',
    }

    KeyPressHandeler=(event)=>{
        
        if(event.key==='Enter'){
            console.log('llll');
            let take={
                id:'3',
                text: event.target.value,
                isSelect:false,
                time:new Date(),
                isCompelete:false
            }
            this.setState({todo:[...this.state.todo,take]})
            console.log(this.state.todo);
        }
    }
    
    toggleForm=()=>{
        this.setState({isOpenModalTodo:!this.state.isOpenModalTodo})
    }
    handerlSearch=()=>{}

    creatTodo=()=>{
        this.creatTodo
    }
render(){
    return(
        
        <div>
        
        <Jumbotron>
            <h3 className='d-flex mx-auto'>React TO-DO</h3>
        </Jumbotron>
        <input type='text' onKeyPress={this.KeyPressHandeler}/>
        {/* <ListView todo={this.state.todo[1]} toggleSelect={this.toggleSelect} toggleComplete={this.toggleComplete}/>
        <Rowview todo={this.state.todo[0]} toggleSelect={this.toggleSelect} toggleComplete={this.toggleComplete} /> */}

        <p>{this.state.todo.length>=1?this.state.todo.map(( to)=>(<div><Rowview todo={to}/></div>)):''}</p>
        <Todofrom creatTodo={()=>{console.log('asbgdfzen')}}/>
        
        <Modal isOpen={this.state.isOpenModalTodo}>
            toggle={this.toggleForm} 
            <ModalHeader toggle={this.toggleForm} >
            Craete todo
            </ModalHeader >
            <ModalBody>
            <Todofrom creatTodo={this.creatTodo}/>
            </ModalBody>
        </Modal>
        </div>
    )
        
    }
}

export default Head;
